import express from "express";
import morgan from "morgan";
import helmet from "helmet";
import cookieParser from "cookie-parser";
import bodyParser from "body-parser";
import passport from "passport";
import mongoose from "mongoose";
import session from "express-session";
import path from "path"
import MongoStore from "connect-mongo"
import "./passport"
import { localsMiddleware } from "./middlewares";
import userRouter from "./routers/userRouter"; // router  default export  .
import globalRouter from "./routers/globalRouter";
import routes from "./routes";
import apiRouter from "./routers/apiRouter";
import imageRouter from "./routers/imageRouter";
import postingRouter from "./routers/postingRouter";

const app = express(); // init   app 
const CookieStore = MongoStore(session);

app.use(helmet());
app.set("view engine","pug"); // express  
app.set("views",path.join(__dirname,"views"))
app.use("/static", express.static(path.join(__dirname,"static")))
app.use(cookieParser());
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended : true }));
app.use(morgan("dev"))
app.use(session({
    secret: process.env.COOKIE_SECRET,
    resave: true,
    saveUninitialized: false,
    store : new CookieStore({mongooseConnection: mongoose.connection})
    //   .  .
}));

app.use(passport.initialize());
app.use(passport.session());




app.use(localsMiddleware);

app.use(routes.home, globalRouter);
app.use(routes.posting, postingRouter);
app.use(routes.image,imageRouter);
app.use(routes.users, userRouter);
app.use(routes.api, apiRouter);



export default app; // appjs  init.js

